import React, { useState } from 'react'
import axios from 'axios';

const SearchBar = () => {

    const [searchInput, setSearchInput] = useState("");
    const [countriesList, setCountriesList] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [cityData, setCityData] = useState(null);
    const countries = 'http://localhost:3000/country'

    const submithandleChange = async () => {
        try {
            const response = await axios.post(`http://localhost:3000/country`, { city: searchInput });
            setCityData(response.data);
            console.log('moedas', response.data)
        } catch (error) {
            console.error('Error fetching data:', error);
            setCurrency(null);
        }
    };

    const handleSearch = async () => {
        try {
            const apiKey = 'YOUR_OPENWEATHERMAP_API_KEY';
            const apiUrl = `https://api.openweathermap.org/data/2.5/weather?q=${searchTerm}&appid=41677c1cc1422d4750faf03753b0dbaa`;

            const response = await axios.get(apiUrl);
            setCityData(response.data);
            console.log('consolando', response.data)
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };
    const [countryCode, setCountryCode] = useState('');
    const [currency, setCurrency] = useState(null);

    const handleSearch2 = async () => {
        try {
            const response = await axios.get(`https://restcountries.com/v2/alpha/${countryCode}`);
            setCurrency(response.data.currencies[0].name);

            console.log('moedas', response.data)
        } catch (error) {
            console.error('Error fetching data:', error);
            setCurrency(null);
        }

    }
    return <div className='mt-4'>

        <div className='container'>
            <div className='row justify-content-md-center'>
                <div className='col-md-4'>
                    <input
                        className='form-control text-center'
                        type="search"
                        placeholder="search the city ..."
                        // onChange={handleChange}
                        onChange={(e) => setSearchInput(e.target.value)}

                        value={searchInput} />

                    <button onClick={submithandleChange}>Pesquisar</button>
                        
                </div>
            </div>
            <div className='row justify-content-md-center'>
                <div className='col-12 col-md-4'>
                    {cityData && (
                        <div className="card mt-5" style={{ minHeight: 150, marginRight: 10, marginLeft: 10 }}>
                            <div className="card-body" >
                                <h5 className="card-title"></h5>
                                <h6 className="card-subtitle mb-2 text-muted">{cityData.name}</h6>
                                <p className="card-text">{cityData.temp}</p>
                                <p className="card-text">{cityData.coin}</p>

                            </div>


                        </div>
                    )}

                </div>
            </div>

        </div>
    </div>


};

export default SearchBar;
