import React, { useState } from 'react';
import axios from 'axios';

const CitySearchWithExchangeRate = () => {
    const [searchTerm, setSearchTerm] = useState('');
    const [cityData, setCityData] = useState(null);
    const [exchangeRate, setExchangeRate] = useState(null);

    const handleSearch = async () => {
        try {
            // First, fetch city data
            const apiKey = '41677c1cc1422d4750faf03753b0dbaa';
            const weatherApiUrl = `https://api.openweathermap.org/data/2.5/weather?q=${searchTerm}&appid=${apiKey}`;
            const weatherResponse = await axios.get(weatherApiUrl);
            setCityData(weatherResponse.data);

            // Extract country code from city data
            const countryCode = weatherResponse.data.sys.country;

            // Then, fetch currency exchange rate data
            const exchangeRateApiUrl = `https://api.exchangeratesapi.io/latest?base=USD&symbols=${countryCode}`;
            const exchangeRateResponse = await axios.get(exchangeRateApiUrl);
            setExchangeRate(exchangeRateResponse.data.rates[countryCode]);
        } catch (error) {
            console.error('Error fetching data:', error);
            setCityData(null);
            setExchangeRate(null);
        }
    };

    return (
        <div>
            <input
                type="text"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                placeholder="Enter city name"
            />
            <button onClick={handleSearch}>Search</button>

            {cityData && (
                <div>
                    <h2>{cityData.name}</h2>
                    <p>Temperature: {cityData.main.temp} Â°C</p>
                </div>
            )}

            {exchangeRate && <p>Exchange Rate: 1 USD = {exchangeRate} {cityData.sys.country} currency</p>}
            {!exchangeRate && <p>No exchange rate data available.</p>}
        </div>
    );
};

export default CitySearchWithExchangeRate;
