import React, { useState } from 'react'
import axios from 'axios';

const SearchBar = () => {

    const [searchInput, setSearchInput] = useState("");
    const [countriesList, setCountriesList] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [cityData, setCityData] = useState(null);
    const countries = [

        { name: "Belgium", continent: "Europe" },
        { name: "India", continent: "Asia" },
        { name: "Bolivia", continent: "South America" },
        { name: "Ghana", continent: "Africa" },
        { name: "Japan", continent: "Asia" },
        { name: "Canada", continent: "North America" },
        { name: "New Zealand", continent: "Australasia" },
        { name: "Italy", continent: "Europe" },
        { name: "South Africa", continent: "Africa" },
        { name: "China", continent: "Asia" },
        { name: "Paraguay", continent: "South America" },
        { name: "Usa", continent: "North America" },
        { name: "France", continent: "Europe" },
        { name: "Botswana", continent: "Africa" },
        { name: "Spain", continent: "Europe" },
        { name: "Senegal", continent: "Africa" },
        { name: "Brazil", continent: "South America" },
        { name: "Denmark", continent: "Europe" },
        { name: "Mexico", continent: "South America" },
        { name: "Australia", continent: "Australasia" },
        { name: "Tanzania", continent: "Africa" },
        { name: "Bangladesh", continent: "Asia" },
        { name: "Portugal", continent: "Europe" },
        { name: "Pakistan", continent: "Asia" },

    ];

    const handleChange = (e) => {
        console.log('o é', e.target.value);

        e.preventDefault();
        setSearchInput(e.target.value);
        if (searchInput.length > 0) {
            setCountriesList(
                countries.filter((country) => {
                    return country.name.toLocaleLowerCase().includes(searchInput.toLocaleLowerCase());
                }))
        }
    };

    const handleSearch = async () => {
        try {
            const apiKey = 'YOUR_OPENWEATHERMAP_API_KEY';
            const apiUrl = `https://api.openweathermap.org/data/2.5/weather?q=${searchTerm}&appid=41677c1cc1422d4750faf03753b0dbaa`;

            const response = await axios.get(apiUrl);
            setCityData(response.data);
            console.log('consolando', response.data)
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };
    const [countryCode, setCountryCode] = useState('');
    const [currency, setCurrency] = useState(null);

    const handleSearch2 = async () => {
        try {
            const response = await axios.get(`https://restcountries.com/v2/alpha/${countryCode}`);
            setCurrency(response.data.currencies[0].name);
        } catch (error) {
            console.error('Error fetching data:', error);
            setCurrency(null);
        }

    }
    return <div className='mt-4'>
        <div>
            <input
                type="text"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                placeholder="Digite o nome da cidade"
            />
            <button onClick={handleSearch}>Pesquisar</button>

            {cityData && (
                <div>
                    <h2>{cityData.name}</h2>
                    <p>Temperatura: {cityData.main.temp} °C</p>
                    {/* Outros dados da cidade podem ser exibidos aqui */}
                </div>
            )}
        </div>

        <div>
            <input
                type="text"
                value={countryCode}
                onChange={(e) => setCountryCode(e.target.value)}
                placeholder="Digite o código do país"
            />
            <button onClick={handleSearch2}>Pesquisar</button>

            {currency && <p>Moeda: {currency}</p>}
            {!currency && <p>Nenhum resultado encontrado.</p>}
        </div>
        <div className='container'>
            <div className='row justify-content-md-center'>
                <div className='col-md-4'>
                    <input
                        className='form-control text-center'
                        type="search"
                        placeholder="search the city ..."
                        onChange={handleChange}
                        value={searchInput} />
                </div>

                {/* <table className="table">
                    <tr>
                        <th>Country</th>
                        <th>Continent</th>
                    </tr>

                    {countriesList.map((country, index) => (
                        <tr key={index}>
                            <td>{country.name}</td>
                            <td>{country.continent}</td>
                        </tr>


                    ))}
                </table> */}

            </div>
            <div className='row justify-content-md-center'>
                {countriesList.map((country, index) => (

                    <div className='col-12 col-md-4'>

                        <div className="card mt-5" style={{ minHeight: 150, marginRight: 10, marginLeft: 10 }}>
                            <div className="card-body" key={index}>
                                <h5 className="card-title"></h5>
                                <h6 className="card-subtitle mb-2 text-muted">{country.name}</h6>
                                <p className="card-text">{country.continent}</p>
                                {/* <a href="#" className="card-link">Card link</a>
                        <a href="#" className="card-link">Another link</a> */}
                            </div>

                        </div>
                    </div>
                ))}

            </div>

        </div>
    </div>


};

export default SearchBar;
